import React, { useState } from 'react';
import { Form, Input, Button, Typography, Layout, message } from 'antd';
import { useNavigate } from 'react-router-dom';
import { login } from '../services/auth';

const { Title } = Typography;
const { Content } = Layout;

const Login = () => {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);

  const onFinish = async (values) => {
    setLoading(true);
    try {
      await login(values.email, values.password);
      message.success('Prijava uspešna!');
      navigate('/dashboard'); // prilagodi po želji
    } catch (error) {
      message.error('Napaka pri prijavi. Preveri podatke.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <Layout style={{ backgroundColor: '#e6f7ff', height: '100vh' }}>
      <Content
        style={{
          maxWidth: 500,
          minHeight: 400,
          margin: '80px auto',
          padding: 32,
          background: '#fff',
          borderRadius: 8,
          boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
          display: 'flex',
          flexDirection: 'column',
          justifyContent: 'center',
        }}
      >
        <Title
          level={2}
          style={{ textAlign: 'center', color: '#004080', fontSize: '2.5rem' }}
        >
          Prijava
        </Title>
        <Form
          name="login"
          layout="vertical"
          onFinish={onFinish}
          requiredMark={false}
        >
          <Form.Item
            label={<span style={{ fontSize: '1.2rem' }}>Email</span>}
            name="email"
            rules={[
              { required: true, message: 'Prosim vnesite email!' },
              { type: 'email', message: 'Prosim vnesite veljaven email!' },
            ]}
          >
            <Input size="large" />
          </Form.Item>

          <Form.Item
            label={<span style={{ fontSize: '1.2rem' }}>Geslo</span>}
            name="password"
            rules={[
              { required: true, message: 'Prosim vnesite geslo!' },
            ]}
          >
            <Input.Password size="large" />
          </Form.Item>

          <Form.Item>
            <Button
              type="primary"
              htmlType="submit"
              block
              loading={loading}
              style={{
                backgroundColor: '#fadb14',
                borderColor: '#fadb14',
                color: '#004080',
                fontSize: '1.2rem',
              }}
            >
              Prijava
            </Button>
          </Form.Item>
        </Form>
      </Content>
    </Layout>
  );
};

export default Login;
